AddCSLuaFile('cl_init.lua') AddCSLuaFile('shared.lua') include('shared.lua') ENT.Model = "" ENT.health = 4ENT.dead = false  ENT.deadtime = 0function ENT:Initialize()    self:SetModel( "models/editor/cube_small.mdl" )    self:SetModelScale(2.2)self:SetHullType ( HULL_TINY )    self:SetHullSizeNormal()    self:SetNoDraw(true) self:SetHealth(self.health)local pos = self:GetBody():GetPos()pos.z = pos.z - 64self:SetPos(pos)end  function ENT:Think()   	local body = self:GetBody()	if !IsValid(body) then		self:Remove() 		return	end	if self.dead and (CurTime()-self.deadtime>1.8 or (CurTime()-self.deadtime>1.0 and self:Health()<-6)) then		self:Killed()	end	local pos = self:GetBody():GetPos()	self.Spine = body:LookupBone("ValveBiped.Bip01_Spine2")	if (self.Spine) then		local matrix = body:GetBoneMatrix(self.Spine)		pos = matrix:GetTranslation()	endpos.z = pos.z - 64self:SetPos(pos)--self:RandomSoundEmit()  end   function ENT:OnTakeDamage(dmg)    self:SetHealth(self:Health() - dmg:GetDamage())     if self:Health() <= 0 && !self.dead then    self.dead = trueself.deadtime = CurTime()--self:Killed()local target=self:GetBody()if target.Springs then    --print("clear springs")	for _, v in pairs(target.Springs) do		SafeRemoveEntityDelayed(v,0)	endendend end  function ENT:Killed()    --local ragdoll = ents.Create( "prop_ragdoll" ) 	--ragdoll:SetModel( self.Model ) 	ragdoll:SetPos( self:GetPos() ) 	--ragdoll:SetSkin( self:GetSkin() ) 	--ragdoll:SetAngles( self:GetAngles() ) 	--ragdoll:Spawn()     --ragdoll:SetCollisionGroup( COLLISION_GROUP_DEBRIS ) 	--ragdoll:GetPhysicsObject():AddVelocity( Vector( math.Rand( -150, 150 ), math.Rand( -150, 150 ), math.Rand( -150, 50 ) ) )     self:Remove() 	 	--undo.Create("Flareon") 		--undo.AddEntity(ragdoll) --for k,v in pairs( player.GetAll()) do undo.SetPlayer(v) end 	undo.Finish() 	 end function ENT:OnRemove()   --timer.Stop("evolve") end